import { createReducer, on } from '@ngrx/store';
import * as loginActions from './login.actions';
const estadoInicial = {
  ok: null,
  mensaje: null,
  usuario: null,
  token: localStorage.getItem('token'),
  exp: null,
  iat: null
}; // Obtener token
// tslint:disable-next-line: variable-name

const _loginReducer = createReducer(estadoInicial, on(loginActions.crearToken, (state, {
  token
}) => {
  const worker = Object.assign(Object.assign({}, state), {
    token
  });
  localStorage.setItem('token', worker.token);
  return worker;
}), on(loginActions.refrescarToken, (state, {
  usuario
}) => {
  const worker = Object.assign(Object.assign({}, usuario), {
    iat: state.iat,
    exp: state.exp
  });
  localStorage.setItem('token', worker.token);
  return worker;
}), on(loginActions.cargarWorker, (state, {
  worker
}) => {
  const usuario = Object.assign(Object.assign({}, worker), {
    token: worker.token
  }); // console.log(usuario);

  return usuario;
}), on(loginActions.obtenerToken, (state, {
  token
}) => {
  const worker = Object.assign(Object.assign({}, state), {
    token
  });
  return worker;
}), on(loginActions.obtenerIdUsuario, (state, {
  usuario
}) => {
  const worker = Object.assign({}, usuario); // console.log(worker);

  return worker;
}), on(loginActions.lognError, state => {
  return state;
}));

export const loginReducer = (state, action) => {
  return _loginReducer(state, action);
};
