import { createReducer, on } from '@ngrx/store';
import * as modalActions from './modal.actions';
const estadoInicial = {
  estado: false,
  tipo: null,
  data: null
}; // tslint:disable-next-line: variable-name

const _modalReducer = createReducer(estadoInicial, on(modalActions.cargarModal, (state, {
  tipo,
  estado,
  data
}) => {
  const modal = {
    estado,
    tipo,
    data
  };
  return modal;
}), on(modalActions.quitarModal, state => {
  const modal = {
    estado: false,
    tipo: null,
    data: null
  };
  return modal;
}));

export const modalReducer = (state, action) => {
  return _modalReducer(state, action);
};
