import * as ayudasActions from './ayuda.actions';
import { createReducer, on } from '@ngrx/store';
const estadoInicial = [{
  estado: null,
  _id: null,
  idCreador: null,
  nombre: null,
  descripcion: null
}]; // tslint:disable-next-line: variable-name

const _ayudaReducer = createReducer(estadoInicial, on(ayudasActions.cargarAyudas, (state, ayudas) => {
  return ayudas.ayudas;
}));

export const ayudaReducer = (state, action) => {
  return _ayudaReducer(state, action);
};
